const fs = require('fs');
const path = require('path');

// Function to create a simple placeholder image
const createPlaceholderImage = () => {
  // This is a minimal valid JPG file (1x1 pixel) encoded as base64
  const base64Image = '';
  
  // Decode the base64 image
  const imageBuffer = Buffer.from(base64Image, 'base64');
  
  // Define the path where we want to save the image
  const imagePath = path.join(__dirname, '../../uploads/sri.jpg');
  
  // Write the image to the file system
  fs.writeFileSync(imagePath, imageBuffer);
  
  console.log(`âœ… Placeholder image created at: ${imagePath}`);
};

// Create the placeholder image
createPlaceholderImage();
